status.calculationScript:
if (BlinkFEP_processing_status=='PROCESSING' && BlinkDSP_running==true) {
    return 'processing';
}
else if ((BlinkFEP_processing_status=='WAITING_FOR_INPUT' && BlinkDSP_running==true) || (BlinkFEP_processing_status=='INITIALISED' && BlinkDSP_running==true)){
    return 'waiting input';
}
else if ((BlinkFEP_processing_status!='PROCESSING' && BlinkDSP_running==true) || (BlinkFEP_processing_status!='STOPPED' && BlinkDSP_running==false)) {
    return 'syncing state';
}
else if (BlinkFEP_processing_status=='STOPPED' && BlinkDSP_running==false)  {
    return 'stopped';
}

mission.calculationScript:
if (BlinkFEP_mission=="DVB-S2_Generic" && BlinkDSP_configuration_name=="DVBS2_VITA.xml") {
    return 'DVB-S2';
}
else if (BlinkFEP_mission=="Suomi" && BlinkDSP_configuration_name=="CCSDS_VITA_Suomi.xml") {
    return 'Suomi';
}
else if (BlinkFEP_mission=="Suomi" && BlinkDSP_configuration_name=="CCSDS_VITA_Suomi_BW350.xml") {
    return 'Suomi 350MHz';
}
else {
    return 'unconfigured';
}
